import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst alias create", async () => {
  const { stdout } = await execaCommand("node dist/cli.js alias create --help");
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js alias create <alias>

    Create an alias

    Options:
          --help        Show help                                          [boolean]
      -p, --project_id  The ID of the project to associate with the alias.
      -t, --task_id     The ID of the task to associate with the alias."
  `);
});
