import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst clients contacts create", async () => {
  const { stdout } = await execaCommand(
    "node dist/cli.js clients contacts create --help",
  );
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js clients contacts create

    Creates a new contact object

    Options:
          --help          Show help                                        [boolean]
      -c, --client_id     The ID of the client associated with this contact.
                                                                          [required]
      -f, --first_name    The first name of the contact.                  [required]
      -l, --last_name     The last name of the contact.
      -t, --title         The title of the contact.
      -e, --email         The contact’s email address.
      -p, --phone_office  The contact’s office phone number.
      -h, --phone_mobile  The contact’s mobile phone number.
      -a, --fax           The contact’s fax number.
      -i, --fields        Comma separated list of fields to display in the output.
      -o, --output        The output format: json, table"
  `);
});
