import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst expenses categories create", async () => {
  const { stdout } = await execaCommand(
    "node dist/cli.js expenses categories create --help",
  );
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js expenses categories create

    Retrieves the company for the currently authenticated user

    Options:
          --help        Show help                                          [boolean]
      -n, --name        The name of the expense category.                 [required]
      -u, --unit_name   The unit name of the expense category.
      -i, --unit_price  The unit price of the expense category.

      -s, --is_active   Whether the expense category is active or archived.

      -f, --fields      Comma separated list of fields to display in the output.
      -o, --output      The output format: json, table"
  `);
});
