import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst invoices messages mark-open-invoice-as-closed", async () => {
  const { stdout } = await execaCommand(
    "node dist/cli.js invoices messages mark-open-invoice-as-closed --help",
  );
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js invoices messages mark-open-invoice-as-closed

    Creates a new invoice message object and marks the invoice as closed

    Options:
          --help        Show help                                          [boolean]
      -i, --invoice_id  The ID of the invoice you're closing.             [required]
      -e, --event_type  Pass “close” to mark the invoice as closed.       [required]
      -f, --fields      Comma separated list of fields to display in the output.
      -o, --output      The output format: json, table"
  `);
});
