import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst note", async () => {
  const { stdout } = await execaCommand("node dist/cli.js note --help");
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js note

    Append notes to a running time entry

    Options:
          --help       Show help                                           [boolean]
      -n, --notes      Notes to be associated with the time entry           [string]
      -o, --overwrite  Overwrite existing notes                            [boolean]"
  `);
});
