import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst open", async () => {
  const { stdout } = await execaCommand("node dist/cli.js open --help");
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js open

    Open documentation or Harvest in your browser

    Commands:
      cli.js open time      View your timesheet
      cli.js open docs      Harvest CLI documentation
      cli.js open accounts  Harvest account switcher
      cli.js open api       Harvest API V2 documentation
      cli.js open expenses  Manage expenses
      cli.js open profile   Edit your profile
      cli.js open reports   Run reports
      cli.js open status    View status of Harvest services

    Options:
      --help  Show help                                                    [boolean]"
  `);
});
