import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst projects get", async () => {
  const { stdout } = await execaCommand("node dist/cli.js projects get --help");
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js projects get

    Retrieves the project with the given ID

    Options:
          --help        Show help                                          [boolean]
      -p, --project_id  The ID of the project you're retrieving.          [required]
      -f, --fields      Comma separated list of fields to display in the output.
      -o, --output      The output format: json, table"
  `);
});
