import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst stop", async () => {
  const { stdout } = await execaCommand("node dist/cli.js stop --help");
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js stop

    Stop a running time entry

    Options:
          --help       Show help                                           [boolean]
      -e, --editor     Launch editor to add notes                          [boolean]
      -n, --notes      Any notes to be associated with the time entry       [string]
      -o, --overwrite  Overwrite existing notes                            [boolean]"
  `);
});
