import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst users billable-rates create", async () => {
  const { stdout } = await execaCommand(
    "node dist/cli.js users billable-rates create --help",
  );
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js users billable-rates create

    Creates a new billable rate object

    Options:
          --help        Show help                                          [boolean]
      -u, --user_id     The ID of the user that you're creating a billable rate for.
                                                                          [required]
      -a, --amount      The amount of the billable rate.                  [required]
      -s, --start_date  The date the billable rate is effective. Cannot be a date in
                         the future.
      -f, --fields      Comma separated list of fields to display in the output.
      -o, --output      The output format: json, table"
  `);
});
