import { execaCommand } from "execa";
import { expect, test } from "vitest";

test("hrvst users cost-rates create", async () => {
  const { stdout } = await execaCommand(
    "node dist/cli.js users cost-rates create --help",
  );
  expect(stdout).toMatchInlineSnapshot(`
    "cli.js users cost-rates create

    Creates a new cost rate object

    Options:
          --help        Show help                                          [boolean]
      -u, --user_id     The ID of the user that you're creating a cost rate for.
                                                                          [required]
      -a, --amount      The amount of the cost rate.                      [required]
      -s, --start_date  The date the cost rate is effective. Cannot be a date in the
                         future.
      -f, --fields      Comma separated list of fields to display in the output.
      -o, --output      The output format: json, table"
  `);
});
